// import { useContext } from "react";
// import { AppContext } from "./context";
import { useGlobalContext } from "./Main";
const News = () => {
  // const[articles,setArticles]=useState([]);
  // const [search,setSearch]=useState("");
  // {6ea1873d933d479f99b97377422a5ccc}
  const {articles}=useGlobalContext();
  
  
  return (
    <>
    <div className="container">
      <div className="row">
  {articles.map((item)=>{
    return(
      
<div className="card m-3" style={{width: "18rem" }}>
  <img src={item.urlToImage} class="card-img-top" alt="NewsImage" />
  <div class="card-body">
    <h5 class="card-title">{item.title ? item.title.slice(0, 66) : ""}</h5>
    <p class="card-text">{item.description ? item.description.slice(0, 111) : ""}</p>
    <small> By {item.author} on {new Date(item.publishedAt).toGMTString()}</small>
    <a href={item.url} target="_blank" class="btn btn-primary">Read More</a>
  </div>
</div>

    )
  })}
  </div>
  </div>
  </>
  )
}

export default News
