import React,{useEffect,useState} from 'react';
import News from '../Components/News';

const Main = () => {
  const[articles,setArticles]=useState([]);
  const [search,setSearch]=useState("general");

  const fetchnews=async ()=>{
    try{
        const url=`https://newsapi.org/v2/everything?q=${search}&apiKey=15229b971c064130806ab63d4fb35172`;
        const res= await fetch(url);
        const data=await res.json();
        setArticles(data.articles);
        console.log(data);
      }
        catch(error)
        {
          console.log(error);
        }
    }
    useEffect(() => {
        fetchnews();
    });
  return (
    <div>Main
      <input type="text" value={search} onChange={(event)=>{if(event.target.value===""){setSearch("general")} else{setSearch(event.target.value);}}}></input>
      {articles.map((item)=>{
        return (
          
<News title={item.title} image={item.urlToImage} length={articles.length}/>
          
        )
      })}
    </div>
  )
}

export default Main
