import { useGlobalContext } from './Main'

const Header = () => {

const {search,onchange}=useGlobalContext();
  return (
    <>  
    
    <div className="container-fluid justify-content-center " style={{backgroundColor:"#111",color:"white",height:"100px"}}>
<div className="container d-flex justify-content-center" >
  <h1>TECH NEWS APP</h1>
</div>

<form className="me-4  d-flex justify-content-center " >
    <input type="text" placeholder="Search Here..." value={search} onChange={(event)=>{onchange(event.target.value)}} ></input> 
     <button onClick={(event)=>{event.preventDefault(); searchNews("")} } >Find News</button>
  </form>
 
    </div>
  
 
  </>

  )
}

export default Header;
