// import React,{useEffect,useState,useContext} from 'react';
// import News from '../Components/News';
// import { useContext } from "react";
// import { AppContext } from "./context";
import { useGlobalContext } from "./Main";
const News = () => {
  // const[articles,setArticles]=useState([]);
  // const [search,setSearch]=useState("");
  // {6ea1873d933d479f99b97377422a5ccc}
  const { articles, isLoading } = useGlobalContext();

  if (isLoading) {
    return (
      <>
        <div className="d-flex justify-content-center mt-3">
          <div className="spinner-border" role="status">
            <span className="visually-hidden ml-2"> Loading...</span>
          </div>
        </div>
      </>
    );
  }
  return (
    <>
      <div className="container">
        <div className="row">
          {articles.map((item) => {
            return (
              <div
                className="card m-3"
                style={{ width: "18rem" }}
                key={item.url}
              >
                <img
                  src={item.urlToImage}
                  className="card-img-top"
                  alt="NewsImage"
                />
                <div className="card-body">
                  <h5 className="card-title">
                    {item.title ? item.title.slice(0, 66) : ""}
                  </h5>
                  <p className="card-text">
                    {item.description ? item.description.slice(0, 111) : ""}
                  </p>
                  <strong>
                    {" "}
                    By {item.author} on{" "}
                    {new Date(item.publishedAt).toGMTString()}
                  </strong>
                  <br></br>
                  <div className="d-flex justify-content-between">
                    <a
                      href={item.url}
                      target="_blank"
                      rel="noreferrer"
                      className="btn btn-primary mt-2 me-2"
                    >
                      Read More
                    </a>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default News;
