import React from 'react'
import { useGlobalContext } from './Main'

const Footer= () => {
    const {page,pageSize,totalResults,getNext,getPrev}=useGlobalContext();
  return (
    <div className="container-fluid d-flex justify-content-center">
        <button >PREV</button>
        <small>{page} of {Math.ceil(totalResults/pageSize)}</small>
        <button >NEXT</button>
    </div>
  )
}

export default Footer
