// import React,{useEffect,useState,useContext} from 'react';
// import News from '../Components/News';
// import { useContext } from "react";
// import { AppContext } from "./context";
import { useGlobalContext } from "./Main";
const News = () => {
  // const[articles,setArticles]=useState([]);
  // const [search,setSearch]=useState("");
  // {6ea1873d933d479f99b97377422a5ccc}
  const {articles}=useGlobalContext();
  
  
  return (
    <>
    <div className="container">
      <div className="row"></div>
  {articles.map((item)=>{
    return(
      
<div class="card" style={{width: "18rem"}}>
  <img src={item.urlToImage} class="card-img-top" alt="NewsImage" />
  <div class="card-body">
    <h5 class="card-title">{item.title}</h5>
    <p class="card-text">{item.description}.</p>
    <a href={item.url} class="btn btn-primary">Read More</a>
  </div>
</div>

    )
  })}
  </div>
  </>
  )
}

export default News
