import React,{createContext,useContext,useReducer,useEffect} from 'react';

const AppContext = createContext();

const AppProvider = ({ children }) => {
      const initialstate={
          search:"general",
          isLoading:true,
          // pageSize:0,
          // page:0,
          articles:[]
      }
      const reducer=(state,action)=>{
        switch(action.type){
        case "GET_STORIES":
          return (
            {...state,articles:action.payload.articles,isLoading:false
            }
          )
          case "SET_LOADING":
            return(
              {...state,isLoading:false}
            )
            case "SEARCH_NEWS":
              return(
                {
                  ...state,
                  query:action.payload
                }
              )
            default:
          return state;
        }
       
      }

          const[state,dispatch]=useReducer(reducer,initialstate);

      const fetchnews=async ()=>{
          try{
              const url=`https://newsapi.org/v2/everything?q=${state.search}&apiKey=15229b971c064130806ab63d4fb35172`;
              const res= await fetch(url);
              const data=await res.json();
              // setArticles(data.articles);
              dispatch({type:"GET_STORIES",
                             payload:{
                              articles:data.articles,

                             },
                            })
              console.log(data);
            }
            catch(error)
            {
              console.log(error);
            }
            
                            dispatch({type:"SET_LOADING",
                            isLoading:true,
                           
                           })
          }
    const searchNews=(search)=>{
      dispatch({
        type:"SEARCH_NEWS",
        payload:search
      })
    }
          useEffect(() => {
              fetchnews();
          },[state.quer]);
  return <AppContext.Provider value={{...state,searchNews}}>{children}</AppContext.Provider>;
};
const useGlobalContext = () => {
  return useContext(AppContext);
};

export {AppContext, AppProvider,  useGlobalContext };
