import React,{createContext,useContext,useReducer,useEffect} from 'react';

const AppContext = createContext();

const AppProvider = ({ children }) => {
      const initialstate={
          search:"general",
          isLoading:true,
          // pageSize:0,
          // page:0,
          articles:[]
      }
      const reducer=(state,action)=>{
        switch(action.type){
        case "GET_STORIES":
          return (
            {...state,articles:action.payload.articles,isLoading:false
            }
          )
          case "SET_LOADING":
            return(
              {...state,isLoading:false}
            )
            case "SEARCH_NEWS":
              return(
                {
                  ...state,
                  search:action.payload,
                }
              )
            default:
          return state;
        }
       
      }

          const[state,dispatch]=useReducer(reducer,initialstate);

      const fetchnews=async (url)=>{
          try{
              // const url=;
              const res= await fetch(url);
              const data=await res.json();
              // setArticles(data.articles);
              dispatch({type:"GET_STORIES",
                             payload:{
                              articles:data.articles,

                             },
                            })
              console.log(data);
            }
            catch(error)
            {
              console.log(error);
            }
            
                            dispatch({type:"SET_LOADING",
                            isLoading:true,
                           
                           })
          }
    const searchNews=(searchnews)=>{
      dispatch({
        type:"SEARCH_NEWS",
        payload:searchnews,
      })
    }
    // eslint-disable-next-line
          useEffect(() => {
              fetchnews(`https://newsapi.org/v2/everything?q=${state.search}&apiKey=edf66adc50674684b9d37df0467f0b3c`);
          },[state.search]);
  return <AppContext.Provider value={{...state,searchNews}}>{children}</AppContext.Provider>;
};
const useGlobalContext = () => {
  return useContext(AppContext);
};

export {AppContext, AppProvider,  useGlobalContext };
