import React from 'react'
import { useGlobalContext } from './Main'
const {query}=useGlobalContext();
const Header = () => {
  return (
    <div className="container-fluid justify-content-center" style={{backgroundColor:"#111",color:"white",height:"100px"}}>
<h1>TECH NEWS APP</h1>
<form>
  <input type="text" value={query} onChange={(event)=>{searchNews(event.target.value)}}></input>
</form>
    </div>
  )
}

export default Header
