import React from 'react';
import './Header.css'
import { useGlobalContext } from './Main'

const Header = () => {

const {search,onchange,searchNews}=useGlobalContext();
  return (
    <>  
    
    <div className="container-fluid mt-0 justify-content-center " style={{backgroundColor:"rgb(22,78,99)",color:"white",height:"100px"}}>
<div className="container-fluid pt-3  border justify-content-center" >
  <h1 >TECH NEWS APP</h1>
  <form className="me-4  mt-0  justify-content-center " >
    <input type="text" placeholder="Search Here..." value={search} onChange={(event)=>{onchange(event.target.value)}} ></input> 
     <button onClick={(event)=>{event.preventDefault(); searchNews({search})} } >Find News</button>
  </form>
</div>


  <div className="container-fluid">
    
  </div>
 
    </div>
  
 
  </>

  )
}

export default Header;
