import React,{useEffect,useState,useContext} from 'react';
import News from '../Components/News';

const Main = () => {
  const[articles,setArticles]=useState([]);
  // const [search,setSearch]=useState("");
  // {6ea1873d933d479f99b97377422a5ccc}
  const fetchnews=async ()=>{
    try{
        const url=`https://newsapi.org/v2/everything?q=microsoft&apiKey=6ea1873d933d479f99b97377422a5ccc`;
        const res= await fetch(url);
        const data=await res.json();
        setArticles(data.articles);
        console.log(data);
      }
        catch(error)
        {
          console.log(error);
        }
    }
    useEffect(() => {
        fetchnews();
    });
  return (
    <div>Main
      {/* <input type="text" value={search} onChange={(event)=>{if(event.target.value===""){setSearch("general")} else{setSearch(event.target.value);}}}></input> */}
      {articles.map((item)=>{
        return (
          
<News title={item.title} image={item.urlToImage} length={articles.length}/>
          
        )
      })}
    </div>
  )
}

export default Main
