import React from 'react'
import { useGlobalContext } from './Main'

const Footer= () => {
    const {page,pageSize,totalResults,getNext,getPrev}=useGlobalContext();
  return (
    <div className="container-fluid d-flex justify-content-center mb-5">
        <button disabled={page<=1}className="btn btn-primary " onClick={()=>getPrev()}>PREV</button>
        <p className="me-3 ms-3 mt-2">{page} of {Math.ceil(totalResults/pageSize)}</p>
        <button disabled={page + 1 > Math.ceil(totalResults / pageSize)} className=" btn btn-primary" onClick={()=>getNext()}>NEXT</button>
    </div>
  )
}

export default Footer
