// import React from 'react'
import React,{ useState } from 'react'
import { createContext } from 'react';
const Header = () => {
  // const {text}=useGlobalContext();
  const newcontext=createContext();
  const[text,setText]=useState("");

  return (
    <>  
      <div className="container-fluid justify-content-center " style={{backgroundColor:"#111",color:"white",height:"100px"}}>
<div className="container d-flex justify-content-center" >
  <h1>TECH NEWS APP</h1>
</div>

<form className="me-4  d-flex justify-content-center " >
    <input type="text" placeholder="Search Here..." value={text} onChange={(event)=>{setText(event.target.value)}} ></input> 
     <button onClick={(event)=>{event.preventDefault(); setText("")} } >Find News</button>
  </form>
 
    </div>
 
  </>

  )
}

export default Header;
